const useState = (initialState) => {
  let state = initialState
  const subscribes = {
    DOM_EFFECT: new Set(),
    USE_EFFECT: new Set(),
    FLOW_EFFECT: new Set(),
  }

  const getState = () => {
    if (stateContext.context) {
      const { type, effectFn } = stateContext.context
      subscribes[type].add(effectFn)
    }
    return state
  }

  const setState = (newState) => {
    if (state === newState) {
      return
    }
    state = newState
    for (const effect of subscribes.DOM_EFFECT) {
      effect()
    }
    for (const effect of subscribes.USE_EFFECT) {
      effect()
    }
    for (const effect of subscribes.FLOW_EFFECT) {
      effect()
    }
  }

  return [state, setState]
}

const App = () => {
  const [show, setShow] = useState(false)
  const [count, setCount] = useState(0)

  return (
    <div>
      <button onClick={() => setShow(!show())}>Toggle</button>
      <button onClick={() => setCount(count() + 1)}>+1</button>
      <Toggle is={show()}>
        <Content count={count()} />
      </Toggle>
    </div>
  )
}

const Content = (props) => {
  return <p>Count: {props.count}</p>
}

const useState = (initialState) => {
  let state = initialState
  const subscribes = {
    DOM_EFFECT: new Set(),
    USE_EFFECT: new Set(),
    FLOW_EFFECT: new Set(),
  }

  const getState = () => {
    if (stateContext.context) {
      const { type, component, effectFn } = stateContext.context
      subscribes[type].add(effectFn)
      if (component) {
        component.unsubscribeEffectHandlers.push(() => {
          subscribes[type].delete(effectFn)
        })
      }
    }
    return state
  }

  const setState = (newState) => {
    if (state === newState) {
      return
    }
    state = newState
    for (const effect of subscribes.DOM_EFFECT) {
      effect()
    }
    for (const effect of subscribes.USE_EFFECT) {
      effect()
    }
    for (const effect of subscribes.FLOW_EFFECT) {
      effect()
    }
  }

  return [state, setState]
}
