import { Button } from '@rvjs/ui'

const ButtonKindExample = () => {
  return div({
    children: [
      Button({
        text: prop(() => 'primary'),
        kind: prop(() => 'primary'),
      }),
      Button({
        text: prop(() => 'secondary'),
        kind: prop(() => 'secondary'),
      }),
      Button({
        text: prop(() => 'tertiary'),
        kind: prop(() => 'tertiary'),
      }),
      Button({
        text: prop(() => 'ghost'),
        kind: prop(() => 'ghost'),
      }),
      Button({
        text: prop(() => 'dangerPrimary'),
        kind: prop(() => 'dangerPrimary'),
      }),
      Button({
        text: prop(() => 'dangerGhost'),
        kind: prop(() => 'dangerGhost'),
      }),
      Button({
        text: prop(() => 'dangerTertiary'),
        kind: prop(() => 'dangerTertiary'),
      }),
    ],
  })
}

export default ButtonKindExample

import { div, prop } from '@rvjs/core'
import { Button, Icon } from '@rvjs/ui'

const ButtonSizeExample = () => {
  return div({
    children: [
      Button({
        text: prop(() => 'sm'),
        kind: prop(() => 'primary'),
        size: prop(() => 'sm'),
        renderIcon: Icon({ type: 'arrow-right' }),
      }),
      Button({
        text: prop(() => 'md'),
        kind: prop(() => 'primary'),
        size: prop(() => 'md'),
        renderIcon: Icon({ type: 'arrow-right' }),
      }),
      Button({
        text: prop(() => 'lg'),
        kind: prop(() => 'primary'),
        size: prop(() => 'lg'),
        renderIcon: Icon({ type: 'arrow-right' }),
      }),
      Button({
        text: prop(() => 'xl'),
        kind: prop(() => 'primary'),
        size: prop(() => 'xl'),
        renderIcon: Icon({ type: 'arrow-right' }),
      }),
      Button({
        text: prop(() => '2xl'),
        kind: prop(() => 'primary'),
        size: prop(() => '2xl'),
        renderIcon: Icon({ type: 'arrow-right' }),
      }),
    ],
  })
}

export default ButtonSizeExample
